mapper.update:
update member
set member_pw = #{member_pw}, member_name = #{member_name}, member_email = #{member_email}
where member_id = #{member_id}

update member
set member_status = "WITHDRAWN"
where member_id = #{member_id}

mapper.select:
select member_id, member_pw, member_name, member_email, salt, member_status, score, member_register_time
from member
where member_id = #{member_id}

select member_pw
from member
where member_id = #{member_id} and member_email = #{member_email}

mapper.update:
update member
set score = score + #{amount}
where member_id = #{member_id}
